function App() {
  const [backgroundColor, setBackgroundColor] = useState('');

  return (
    <div id = "root" className="App" style={{ backgroundColor }}>
      <header className="App-header">
        <h1> This is a background color picker </h1>
        <BackgroundColorInputForm setBackgroundColor={setBackgroundColor} />
      </header>
    </div>
  );
}


