import { useState } from 'react';
import './App.css';

function ColorInputForm() {
  //define a variable and function that stores input and 
  const [color, setColor] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    alert('The color you picked is: ${color}');
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>Enter a color in Hexical code:
        <input>
          type="text"
          value={color}
          onChange={(e) => setColor(e.target.value)}
        </input>
      </label>
    </form>
  )
} 


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1> This is a background color picker </h1>
      </header>
    </div>
  );
}

export default App;
