import { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './App.css';

function ColorInputForm() {
  //define a variable and function that stores input and reacts to the input
  const [color, setColor] = useState("");

  const handleSubmit = (event) => {
    // when user has input a color, this function will be triggered
    event.preventDefault();
    alert('The color you picked is: ${color}');
  }

  return (
    // triggered function handleSubmit() when user submits
    <form onSubmit={handleSubmit}>
      <label>Enter a color in Hexical code:
        <input type="submit">
          type="text"
          value={color}
          onChange={(e) => setColor(e.target.value)}
        </input>
      </label>

    </form>
  )
} 


function App() {
  return (
    <div id = "root" className="App">
      <header className="App-header">
        <h1> This is a background color picker </h1>
      </header>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));

export default App;
